mapper.insert:

  SELECT LAST_INSERT_ID()

insert into my_award (award_id, user_id, total,game_id
  )
values (#{awardId,jdbcType=INTEGER}, #{userId,jdbcType=VARCHAR}, #{total,jdbcType=INTEGER}
  )

mapper.select:
select id, award_id, user_id, total,game_id
from my_award
where id = #{id,jdbcType=INTEGER}

select id, award_id, user_id, total,game_id
from my_award
where award_id = #{awardId} and user_id=#{userId}

select t.counts
from
	( select count(*) counts,user_id from my_award 	where award_id !=22 group by user_id
	) t
where user_id = #{user_id,jdbcType=VARCHAR}
