mapper.insert:

  SELECT LAST_INSERT_ID()

insert into option (question_id, option, name
  )
values (#{questionId,jdbcType=INTEGER}, #{option,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}
  )

mapper.update:
update option
set question_id = #{questionId,jdbcType=INTEGER},
  option = #{option,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, question_id, option, name
from option
where id = #{id,jdbcType=INTEGER}
