mapper.insert:

  SELECT LAST_INSERT_ID()

insert into options (question_id, choices, name
  )
values (#{questionId,jdbcType=INTEGER}, #{choices,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}
  )

mapper.update:
update options
set question_id = #{questionId,jdbcType=INTEGER},
  choices = #{choices,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, question_id, choices, name
from options
where id = #{id,jdbcType=INTEGER}
