mapper.insert:

  SELECT LAST_INSERT_ID()

insert into times (times, user_id, date
  )
values (#{times,jdbcType=INTEGER}, #{userId,jdbcType=VARCHAR}, #{date,jdbcType=VARCHAR}
  )

mapper.update:
update times
set times = #{times,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=VARCHAR},
  date = #{date,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, times, user_id, date
from times
where id = #{id,jdbcType=INTEGER}

select id, times, user_id, date
from times
where user_id = #{userId} and date =#{date}
